import React, { Component } from "react";

class Modal extends Component {
    state = {
        modalShow:false,
    }

    closeModalHandler = () => {
        this.setState({
            modalShow:false
        })
    }

    componentDidMount(){
        console.log(this.props)
        this.setState({
            modalShow:this.props.modalState
        })
    }

    render() {

        let modalStyleClass = (this.state.modalShow===true)?"block":"none";
        console.log(modalStyleClass)
        return (
            <React.Fragment>
                <div style={{display:modalStyleClass,backgroundColor:'rgba(0, 0, 0, 0.2)'}} className="modal" role="dialog">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Modal title</h5>
                            <button onClick={this.closeModalHandler} type="button" className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <p>Modal body text goes here.</p>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-primary">Save changes</button>
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }

}


export default Modal;
