import React, {Component} from 'react';
import Modal from '../Modal/Modal';
import FormList from './FormList/FormList';

const slugify = require("slugify");

class FormManager extends Component {

    state = {
        form_list:[],
        modal_state:true
    }

    testFormObjectList = [
        {
            id:slugify("Test form"),
            "name": "Test form",
            "description": "Uye bilgi formu",
            createdAt: "2017-01-08",
            fields: [ 
                { "required": true, "name": "Ad", dataType: "STRING" }, 
                { "required": true, "name": "Soyad", dataType: "STRING" },
                { "required": false, "name": "Yaş", dataType: "NUMBER" } 
            ]
        },
        {
            id:slugify("Test form2"),
            "name": "Test form2",
            "description": "Uye bilgi formu",
            createdAt: "2017-01-08",
            fields: [ 
                { "required": true, "name": "Ad", dataType: "STRING" }, 
                { "required": true, "name": "Soyad", dataType: "STRING" },
                { "required": false, "name": "Yaş", dataType: "NUMBER" } 
            ]
        },
    ]

    initCreateForms = () => {
        if(!localStorage.getItem("form_list")){
            localStorage.setItem("form_list",JSON.stringify(this.testFormObjectList));
        }
        let init_form_list = JSON.parse(localStorage.getItem("form_list"));
        this.setState({
            form_list:init_form_list
        })   
    }

    addFomHandler = () => {
        this.setState({
            ...this.state,
            modal_state:true
        })
    }


    componentDidMount() {
        this.initCreateForms()
    }

    render() {
        console.log(this.state)
        return (
            <React.Fragment>
                <div className="mt-3">
                    <button onClick={this.addFomHandler} className="btn btn-success float-right mb-2">Add Form</button>
                    <FormList formList={this.state.form_list}></FormList>
                </div>
                <Modal modalState={this.state.modal_state}></Modal>
            </React.Fragment>
        )
    }

}

export default FormManager;
